with failed as (
  SELECT count(*) as failed_count FROM describe_connection_jobs where $__timeFilter(created_at) AND connection_id like $account_id AND status IN ('TIMEOUT', 'FAILED')
), total as (
  SELECT count(*) as total_count FROM describe_connection_jobs where $__timeFilter(created_at) AND connection_id like $account_id AND status IN ('TIMEOUT', 'FAILED', 'SUCCEEDED')
)
select sum(failed_count) / sum(total_count) * 100.0  from (select failed_count, 0 as total_count from failed union select 0 as failed_count, total_count from total) as result
